import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section id="hero" className="flex items-center justify-center h-screen bg-dark-bg text-light-text">
      <div className="text-center">
        <motion.h1 
          className="text-5xl md:text-7xl font-bold mb-4"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          Hello, I'm Ravishankar Gharabidi
        </motion.h1>
        <motion.p className="text-xl md:text-2xl text-dark-text mb-8"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}>AI/ML Engineer & Software Developer</motion.p>
        <a href="#projects" className="inline-block px-8 py-3 bg-primary text-white rounded-full shadow-lg hover:bg-primary-hover transition-colors duration-300">
          View My Work
        </a>
      </div>
    </section>
  );
};

export default Hero;
