import React from 'react';
import { FaCertificate } from 'react-icons/fa';

const Certifications = () => {
  const certs = [
    { name: 'Oracle Cloud Infrastructure 2025 Certified AI Foundations Associate', issuer: 'Oracle', date: 'Aug 2025' },
    { name: 'Oracle Fusion AI Studio Foundations Associate – Rel 1', issuer: 'Oracle', date: 'Aug 2025' },
    { name: 'AI Fundamentals with IBM SkillsBuild', issuer: 'IBM & Cisco', date: 'Sep 2025' },
    { name: 'Computer Networks and Internet Protocol', issuer: 'IIT Kharagpur (NPTEL)', date: 'Apr 2024' },
    { name: 'Introduction to Internet of Things', issuer: 'IIT Kharagpur (NPTEL)', date: 'Oct 2025' },
    { name: 'Power BI for Beginners: Zero to Hero Fast Track', issuer: 'Self-paced', date: 'Jan 2026' },
    { name: 'Certificate of Appreciation – Scroll Hack Hackathon', issuer: 'Scroll Hack', date: 'Aug 2024' },
  ];

  return (
    <section id="certifications" className="py-20 bg-dark-card text-light-text">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-12">Certifications & Achievements</h2>
        <div className="max-w-3xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8">
          {certs.map((cert, index) => (
            <div key={index} className="flex items-start space-x-4 p-4 rounded-lg hover:bg-gray-700/50 transition-colors">
              <FaCertificate className="text-primary mt-1 flex-shrink-0" size={24} />
              <div>
                <h3 className="text-lg font-semibold">{cert.name}</h3>
                <p className="text-dark-text text-sm">
                  {cert.issuer} &bull; {cert.date}
                </p>
                {/* If you have a link, you can add it here */}
                {/* <a href={cert.link} className="text-primary text-sm hover:underline">View Certificate</a> */}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Certifications;
