import React from 'react';

const Certifications = () => {
  const certs = [
    { name: 'Power BI Certification', issuer: 'Udemy', link: 'https://www.udemy.com/certificate/link' },
    // Add others from resume
  ];

  return (
    <section id="certifications" className="py-20 bg-white">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-10">Certifications</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {certs.map((cert, index) => (
            <div key={index} className="bg-gray-100 p-6 rounded-lg shadow">
              <h3 className="text-xl font-semibold mb-2">{cert.name}</h3>
              <p className="mb-4">Issued by {cert.issuer}</p>
              <a href={cert.link} className="text-blue-600 hover:underline">View Certificate</a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Certifications;
