import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <section id="about" className="py-20 bg-dark-card text-light-text">
      <div className="container mx-auto px-4 flex flex-col md:flex-row items-center gap-10">
        <motion.img 
          src="https://via.placeholder.com/150" // Replace with your actual photo path e.g., /ravishankar-gharabidi.jpg
          alt="Ravishankar Gharabidi" 
          className="w-48 h-48 rounded-full object-cover shadow-lg"
          initial={{ scale: 0.5, opacity: 0 }}
          whileInView={{ scale: 1, opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}/>
        <div className="text-center md:text-left">
          <h2 className="text-4xl font-bold mb-4">About Me</h2>
          <p className="text-lg text-dark-text mb-6 max-w-2xl">
            A forward-thinking and collaborative Computer Science student specializing in AI. I am passionate about leveraging my technical expertise in AI/ML and software development to build innovative solutions, foster professional growth, and contribute meaningfully to achieving strategic goals.
          </p>
          <a href="/Resume for AIML & Software Development.pdf" download className="inline-block px-6 py-3 bg-primary text-white rounded-full hover:bg-primary-hover transition-colors">
          Download Resume
        </a>
      </div>
      </div>
    </section>
  );
};

export default About;
