import React from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin } from 'react-icons/fa';

const Footer = () => {
  const year = new Date().getFullYear();

  return (
    <footer className="bg-gradient-to-t from-dark-bg to-dark-card text-dark-text py-8 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-radial from-primary/5 via-transparent to-secondary/5"></div>
      <div className="container mx-auto px-4 text-center relative z-10">
        <motion.div
          className="flex justify-center space-x-6 mb-6"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <motion.a
            href="https://github.com/Ravi19092004"
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-primary transition-colors"
            whileHover={{ scale: 1.2, rotate: 360 }}
            transition={{ duration: 0.6 }}
          >
            <FaGithub size={28} />
          </motion.a>
          <motion.a
            href="https://www.linkedin.com/in/ravishankar-gharabidi-35bb2b262/"
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-primary transition-colors"
            whileHover={{ scale: 1.2, rotate: 360 }}
            transition={{ duration: 0.6 }}
          >
            <FaLinkedin size={28} />
          </motion.a>
          {/* Add other social links here */}
        </motion.div>
        <motion.p
          className="text-sm"
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          &copy; {year} Ravishankar Gharabidi. All Rights Reserved.
        </motion.p>
        <motion.div
          className="mt-4 text-xs text-dark-text/70"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          Built with ❤️ using React & Tailwind CSS
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
