import React from 'react';

const Hero = () => {
  return (
    <section id="hero" className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-500 to-purple-600 text-white">
      <div className="text-center animate-fade-in">
        <h1 className="text-5xl font-bold mb-4">Hello, I'm [Your Name]</h1>
        <p className="text-xl mb-6">Aspiring Developer & Data Analyst</p>
        <a href="#projects" className="inline-block px-6 py-3 bg-white text-blue-600 rounded-full shadow-lg hover:bg-gray-200 transition duration-300">
          View My Work
        </a>
      </div>
    </section>
  );
};

export default Hero;
