import React, { useState } from 'react';

const Contact = () => {
  const [form, setForm] = useState({ name: '', email: '', message: '' });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send to email)
    alert('Message sent!');
  };

  return (
    <section id="contact" className="py-20 bg-gray-100">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-10">Contact Me</h2>
        <form onSubmit={handleSubmit} className="max-w-md mx-auto">
          <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} className="w-full p-3 mb-4 border rounded" required />
          <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} className="w-full p-3 mb-4 border rounded" required />
          <textarea name="message" placeholder="Message" value={form.message} onChange={handleChange} className="w-full p-3 mb-4 border rounded" required></textarea>
          <button type="submit" className="w-full px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition">Send</button>
        </form>
        <div className="text-center mt-6">
          <a href="https://github.com/yourusername" className="text-blue-600 hover:underline">Visit My GitHub</a>
        </div>
      </div>
    </section>
  );
};

export default Contact;
