import React from 'react';
import { motion } from 'framer-motion';

const projects = [
  {
    title: 'JashnMart – Full-Stack E-Commerce Application',
    description: 'Built a responsive full-stack e-commerce platform with secure payment integration via Stripe, product browsing, cart management, and personalized shopping experiences.',
    tech: ['React', 'Node.js', 'Express.js', 'MongoDB', 'Stripe', 'Tailwind CSS', 'DaisyUI'],
    github: 'https://github.com/Ravi19092004/JashnMart', // Placeholder
  },
  {
    title: 'ML-Powered Browser Extension',
    description: 'Designed a machine learning classifier to detect unsafe websites using URL/domain features. Integrated into a Chrome extension for real-time safety detection with caching for performance.',
    tech: ['Python', 'TensorFlow', 'Scikit-learn', 'JavaScript', 'Chrome Extension API', 'SQLite'],
    github: 'https://github.com/Ravi19092004/ML-Powered-Browser-Extension', // Placeholder
  },
  {
    title: 'Authentication Service Provider',
    description: 'Developed a secure authentication system using Next.js and Prisma with robust user access/session management. Implemented OAuth for Google & GitHub logins and email verification via Resend API.',
    tech: ['Next.js', 'Prisma', 'OAuth', 'Resend API', 'TypeScript'],
    github: 'https://github.com/Ravi19092004/Authentication-Service-Provider', // Placeholder
  },
];

const cardVariants = {
  hidden: { y: 50, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      type: 'spring',
      bounce: 0.4,
      duration: 0.8
    }
  }
};

const Projects = () => {
  return (
    <section id="projects" className="py-20 bg-dark-bg text-light-text">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-12">My Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={index}
              className="bg-dark-card rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300"
              variants={cardVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true, amount: 0.5 }}
            >
              <div className="p-6">
                <h3 className="text-2xl font-bold mb-3 text-primary">{project.title}</h3>
                <p className="text-dark-text mb-4">{project.description}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.tech.map((tech, i) => (
                    <span key={i} className="bg-gray-700 text-xs font-semibold px-2.5 py-1 rounded-full">{tech}</span>
                  ))}
                </div>
                <a href={project.github} target="_blank" rel="noopener noreferrer" className="text-primary hover:underline">
                  View on GitHub &rarr;
                </a>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
