import React, { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls } from "@react-three/drei";

/* ---------------- PERSON ---------------- */
function Person() {
  const headRef = useRef();
  const leftArm = useRef();
  const rightArm = useRef();

  useFrame(({ clock }) => {
    const t = clock.elapsedTime;

    // Head slight movement
    headRef.current.rotation.y = Math.sin(t) * 0.1;

    // Typing arms animation
    leftArm.current.rotation.x = Math.sin(t * 5) * 0.3;
    rightArm.current.rotation.x = Math.cos(t * 5) * 0.3;
  });

  return (
    <group position={[0, 0.6, 0.5]}>
      {/* Head */}
      <mesh ref={headRef} position={[0, 1.4, 0]}>
        <sphereGeometry args={[0.25, 16, 16]} />
        <meshStandardMaterial color="#facc15" />
      </mesh>

      {/* Body */}
      <mesh position={[0, 0.7, 0]}>
        <boxGeometry args={[0.5, 0.9, 0.3]} />
        <meshStandardMaterial color="#3b82f6" />
      </mesh>

      {/* Left Arm */}
      <mesh ref={leftArm} position={[-0.35, 0.8, 0.2]}>
        <boxGeometry args={[0.15, 0.6, 0.15]} />
        <meshStandardMaterial color="#3b82f6" />
      </mesh>

      {/* Right Arm */}
      <mesh ref={rightArm} position={[0.35, 0.8, 0.2]}>
        <boxGeometry args={[0.15, 0.6, 0.15]} />
        <meshStandardMaterial color="#3b82f6" />
      </mesh>

      {/* Legs */}
      <mesh position={[-0.15, 0.1, 0]}>
        <boxGeometry args={[0.15, 0.5, 0.15]} />
        <meshStandardMaterial color="#1f2937" />
      </mesh>

      <mesh position={[0.15, 0.1, 0]}>
        <boxGeometry args={[0.15, 0.5, 0.15]} />
        <meshStandardMaterial color="#1f2937" />
      </mesh>
    </group>
  );
}

/* ---------------- COMPUTER ---------------- */
function Computer() {
  const keyboardRef = useRef();

  useFrame(({ clock }) => {
    keyboardRef.current.position.y =
      0.55 + Math.sin(clock.elapsedTime * 8) * 0.02;
  });

  return (
    <group position={[0, 0.6, -0.4]}>
      {/* Monitor */}
      <mesh position={[0, 0.6, -0.4]}>
        <boxGeometry args={[0.9, 0.5, 0.05]} />
        <meshStandardMaterial color="#111827" />
      </mesh>

      {/* Screen */}
      <mesh position={[0, 0.6, -0.37]}>
        <boxGeometry args={[0.8, 0.4, 0.01]} />
        <meshStandardMaterial color="#22d3ee" />
      </mesh>

      {/* Keyboard */}
      <mesh ref={keyboardRef} position={[0, 0.55, 0.1]}>
        <boxGeometry args={[0.7, 0.05, 0.25]} />
        <meshStandardMaterial color="#374151" />
      </mesh>
    </group>
  );
}

/* ---------------- DESK ---------------- */
function Desk() {
  return (
    <group>
      <mesh position={[0, 0.5, 0]}>
        <boxGeometry args={[2, 0.1, 1]} />
        <meshStandardMaterial color="#92400e" />
      </mesh>
    </group>
  );
}

/* ---------------- CHAIR ---------------- */
function Chair() {
  return (
    <group position={[0, 0.3, 0.6]}>
      <mesh>
        <boxGeometry args={[0.6, 0.1, 0.6]} />
        <meshStandardMaterial color="#4b5563" />
      </mesh>
      <mesh position={[0, 0.4, -0.25]}>
        <boxGeometry args={[0.6, 0.6, 0.1]} />
        <meshStandardMaterial color="#4b5563" />
      </mesh>
    </group>
  );
}

/* ---------------- SCENE ---------------- */
export default function PersonUsingComputer() {
  return (
    <div className="w-full h-[500px]">
      <Canvas camera={{ position: [3, 2, 4], fov: 45 }}>
        <ambientLight intensity={0.6} />
        <directionalLight position={[5, 5, 5]} intensity={1} />

        <Desk />
        <Computer />
        <Chair />
        <Person />

        <OrbitControls enableZoom={false} />
      </Canvas>
    </div>
  );
}
