import React from 'react';

const About = () => {
  return (
    <section id="about" className="py-20 bg-gray-100">
      <div className="container mx-auto px-4 text-center">
        <h2 className="text-3xl font-bold mb-6">About Me</h2>
        <p className="text-lg mb-6">Brief description from your resume: [Insert your bio here].</p>
        <img src="/path/to/your/photo.jpg" alt="Your Photo" className="w-32 h-32 rounded-full mx-auto mb-6" />
        <a href="/resume.pdf" download className="inline-block px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition">
          Download Resume
        </a>
      </div>
    </section>
  );
};

export default About;
