import React from 'react';
import { FaGithub, FaLinkedin, FaEnvelope } from 'react-icons/fa';

const Contact = () => {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [status, setStatus] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus('Sending...');
    // Replace with your Formspree endpoint
    const response = await fetch('https://formspree.io/f/your_form_id', {
      method: 'POST',
      body: JSON.stringify(form),
      headers: {
        'Accept': 'application/json'
      }
    });

    if (response.ok) {
      setStatus('Message sent successfully!');
      setForm({ name: '', email: '', message: '' });
    } else {
      setStatus('Failed to send message. Please try again.');
    }
  };

  return (
    <section id="contact" className="py-20 bg-dark-bg text-light-text">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-12">Get In Touch</h2>
        <form onSubmit={handleSubmit} className="max-w-xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <input type="text" name="name" placeholder="Your Name" value={form.name} onChange={handleChange} className="w-full p-3 bg-dark-card border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-primary" required />
            <input type="email" name="email" placeholder="Your Email" value={form.email} onChange={handleChange} className="w-full p-3 bg-dark-card border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-primary" required />
          </div>
          <textarea name="message" placeholder="Your Message" value={form.message} onChange={handleChange} rows="5" className="w-full p-3 mb-6 bg-dark-card border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-primary" required></textarea>
          <button type="submit" className="w-full px-6 py-3 bg-primary text-white font-bold rounded-md hover:bg-primary-hover transition-colors">
            Send Message
          </button>
          {status && <p className="text-center mt-4">{status}</p>}
        </form>
      </div>
    </section>
  );
};

export default Contact;
